import { INCREMENT, DECREMENT, CUSTOM } from "./action";

export const increment = (number = 0) => {
  return {
    type: INCREMENT,
    payload: number,
  };
};

export const decrement = (number) => {
  return {
    type: DECREMENT,
    payload: number,
  };
};
